namespace codec {
  /**
   * Analog 0x30 (keep alive) frame parser
   */
  export class Analog20x30Parser implements FrameParser {
    readonly deviceType = 'analog2';
    readonly frameCode = 0x30;

    private parser = new Analog20x42Parser();

    public parseFrame(payload: Buffer, configuration: Buffer, network: Network) {
      const appContent = this.parser.parseFrame(payload, configuration, network);

      appContent['type'] = '0x30 Analog V2 keep alive';

      return appContent;
    }
  }
}
