namespace codec {
  /**
   * Analog V2 0x38 (keep alive) frame parser
   */
  export class Analog20x38Parser implements FrameParser {
    readonly deviceType = 'analog2';
    readonly frameCode = 0x38;

    private parser = new Analog20x71Parser();

    public parseFrame(payload: Buffer, configuration: Buffer, network: Network) {
      const appContent = this.parser.parseFrame(payload, configuration, network);

      appContent['type'] = '0x38 Analog V2 keep alive';

      return appContent;
    }
  }
}
