/// <reference path='status-byte.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('breathStatusByteParser', () => {
  let parser: codec.BreathStatusByteParser;

  beforeEach(() => {
    parser = new codec.BreathStatusByteParser();
  });

  it('should parse 20200501', () => {
    const payloadString = '20200501';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');

    expect(content['status']['frameCounter']).to.equal(1);
    expect(content['status']['configurationInconsistency']).to.equal(false);
    expect(content['status']['sensorError']).to.equal(false);
  });
});
