/// <reference path='0x10.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('ComfortSerenity0x10Parser', () => {
  let parser: codec.ComfortSerenity0x10Parser;

  beforeEach(() => {
    parser = new codec.ComfortSerenity0x10Parser();
  });

  it('should parse frame and return correct config', () => {
    const payloadString = '1010000100010001070800';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x10 Comfort Serenity configuration');
    expect(content['dailyFrameActivated']).to.equal(true);
    expect(content['numberOfHistorizationBeforeSending']).to.equal(1);
    expect(content['numberOfSamplingBeforeHistorization']).to.equal(1);
    expect(content['samplingPeriod'].value).to.equal(3600);
    expect(content['redundantSamples']).to.equal(0);
    expect(content['calculatedPeriodRecording'].value).to.equal(3600);
    expect(content['calculatedSendingPeriod'].value).to.equal(3600);
  });
  it('should parse frame and return correct config', () => {
    const payloadString = '1010000100010001070800010C';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x10 Comfort Serenity configuration');
    expect(content['dailyFrameActivated']).to.equal(true);
    expect(content['numberOfHistorizationBeforeSending']).to.equal(1);
    expect(content['numberOfSamplingBeforeHistorization']).to.equal(1);
    expect(content['samplingPeriod'].value).to.equal(3600);
    expect(content['redundantSamples']).to.equal(0);
    expect(content['calculatedPeriodRecording'].value).to.equal(3600);
    expect(content['calculatedSendingPeriod'].value).to.equal(3600);
    expect(content['blackOutDuration'].value).to.equal(1);
    expect(content['blackOutStartTime'].value).to.equal(12);
  });
});
