/// <reference path='0x41.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Drycontacts0x41Parser', () => {
  let parser: codec.Drycontacts20x41Parser;

  beforeEach(() => {
    parser = new codec.Drycontacts20x41Parser();
  });

  it('should parse 4140030014', () => {
    const payloadString = '4140030014';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x41 Dry Contacts 2 duration alarm');
    expect(content['channel']).to.equal(3);
    expect(content['durationThreshold'].value).to.equal(20);
  });

  it('should parse 414403001414ABA3E9', () => {
    const payloadString = '414403001414ABA3E9';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x41 Dry Contacts 2 duration alarm');
    expect(content['timestamp']).to.equal('2023-12-28T19:22:17.000');
    expect(content['channel']).to.equal(3);
    expect(content['durationThreshold'].value).to.equal(20);
  });
});
