/// <reference path='0x36.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Generic0x36Parser', () => {
  let parser: codec.Generic0x36Parser;

  beforeEach(() => {
    parser = new codec.Generic0x36Parser();
  });

  it('should parse 362000', () => {
    const payloadString = '362000';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x36 Alert message');
    expect(content['alertCode']).to.equal('normalState');
  });

  it('should parse 364001', () => {
    const payloadString = '364001';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x36 Alert message');
    expect(content['alertCode']).to.equal('uplinkDownlinkForbidden');
  });
});
