namespace codec {
  /**
   * Generic 0x36 alert message frame parser
   */
  export class Generic0x36Parser implements FrameParser {
    readonly deviceType = 'temp4';
    readonly frameCode = 0x36;

    public parseFrame(payload: Buffer, configuration: Buffer, network: Network) {
      const appContent: Content = { type: '0x36 Alert message' };

      switch (payload[2]) {
        case 0x00:
          appContent['alertCode'] = 'normalState';
          break;
        case 0x01:
          appContent['alertCode'] = 'uplinkDownlinkForbidden';
          break;
        default:
          break;
      }

      return appContent;
    }
  }
}
