/// <reference path='0x37.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Generic0x37Parser', () => {
  let parser: codec.Generic0x37Parser;

  beforeEach(() => {
    parser = new codec.Generic0x37Parser();
  });

  it('should parse 3720020100020001', () => {
    const payloadString = '3720020100020001';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x37 Software version');
    expect(content['appVersion']).to.equal('2.1.0');
    expect(content['rtuVersion']).to.equal('2.0.1');
  });
});
