namespace codec {
  /**
   * Generic 0x37 Software version' frame parser
   */
  export class Generic0x37Parser implements FrameParser {
    readonly deviceType = 'temp4|comfort2|comfortCo2|breath|comfortSerenity|modbus';
    readonly frameCode = 0x37;

    public parseFrame(payload: Buffer, configuration: Buffer, network: Network) {
      const appContent: Content = { type: '0x37 Software version' };
      appContent['appVersion'] = payload.readUInt8(2) + '.' + payload.readUInt8(3) + '.' + payload.readUInt8(4);
      appContent['rtuVersion'] = payload.readUInt8(5) + '.' + payload.readUInt8(6) + '.' + payload.readUInt8(7);

      return appContent;
    }
  }
}
