/// <reference path='0x10.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Modbus0x10Parser', () => {
  let parser: codec.Modbus0x10Parser;

  beforeEach(() => {
    parser = new codec.Modbus0x10Parser();
  });

  it('should parse frame and return correct config', () => {
    const payloadString = '101021C021C100B4440100';
    const content = parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x10 Modbus configuration');
    expect(content['transmissionPeriodKeepAlive'].value).to.equal(86400);
    expect(content['transmissionPeriod1'].value).to.equal(86410);
    expect(content['samplingPeriod'].value).to.equal(1800);
    expect(content['modbusConfiguration']).to.equal(68);
    expect(content['supplyTime'].value).to.equal(25.6);
  });
});
