/// <reference path='0x5f.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Modbus0x5fParser', () => {
  let parser: codec.Modbus0x5fParser;

  beforeEach(() => {
    parser = new codec.Modbus0x5fParser();
  });

  it('should parse 5f02ffffffff000000aaffffff80', () => {
    const content = parser.parseFrame(Buffer.from('5f02ffffffff000000aaffffff80', 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x5f Modbus data (int32)');
    expect(content['registerValues'][0]).to.equal(-1);
    expect(content['registerValues'][1]).to.equal(170);
    expect(content['registerValues'][2]).to.equal(-128);
  });
});
