/// <reference path='0x61.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Modbus0x61Parser', () => {
  let parser: codec.Modbus0x61Parser;

  beforeEach(() => {
    parser = new codec.Modbus0x61Parser();
  });

  it('should parse 61023f9e041840161e4f', () => {
    const content = parser.parseFrame(Buffer.from('61023f9e041840161e4f', 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x61 Modbus data (float)');
    expect(content['registerValues'][0]).to.equal(1.2344999313354492);
    expect(content['registerValues'][1]).to.equal(2.345599889755249);
  });
});
