/// <reference path='0x62.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('Modbus0x62Parser', () => {
  let parser: codec.Modbus0x62Parser;

  beforeEach(() => {
    parser = new codec.Modbus0x62Parser();
  });

  it('should parse 6202ffffffff000000aaffffff80', () => {
    const content = parser.parseFrame(Buffer.from('6202ffffffff000000aaffffff80', 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x62 Modbus data (uint32)');
    expect(content['registerValues'][0]).to.equal(4294967295);
    expect(content['registerValues'][1]).to.equal(170);
    expect(content['registerValues'][2]).to.equal(4294967168);
  });
});
