/// <reference path='0x46.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('PulseV4NbIot0x46Parser', () => {
  let pulse0x46Parser: codec.PulseV4NbIot0x46Parser;

  beforeEach(() => {
    pulse0x46Parser = new codec.PulseV4NbIot0x46Parser();
  });

  it('should parse 0x46 frame', () => {
    const payloadString = '1122334455667788030000000046300000009f0000000214ABA3E9';
    const content = pulse0x46Parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x46 Pulse 4 NB-IoT data');
    expect(content['counterValues'][0]).to.equal(159);
    expect(content['counterValues'][1]).to.equal(2);
  });

  it('should parse 0x46 frame with framecode', () => {
    const payloadString = '1122334455667788030000000046340000009f0000000214ABA3E9';
    const content = pulse0x46Parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x46 Pulse 4 NB-IoT data');
    expect(content['counterValues'][0]).to.equal(159);
    expect(content['counterValues'][1]).to.equal(2);
    expect(content['timestamp']).to.equal('2023-12-28T19:22:17.000');
  });
});
