/// <reference path='0x47.parser.ts' />

import { expect } from 'chai';
import 'mocha';

describe('PulseV4NbIot0x47Parser', () => {
  let pulse0x47Parser: codec.PulseV4NbIot0x47Parser;

  beforeEach(() => {
    pulse0x47Parser = new codec.PulseV4NbIot0x47Parser();
  });

  it('should parse 0x47 frame', () => {
    const payloadString = '1122334455667788030000000047a00f780000';
    const content = pulse0x47Parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x47 Pulse 4 NB-IoT alarm');
    expect(content['flowValues'][0]).to.equal(3960);
    expect(content['flowValues'][1]).to.equal(0);
  });
  it('should parse 0x47 frame with timestamp', () => {
    const payloadString = '1122334455667788030000000047340f78000014ABA3E9';
    const content = pulse0x47Parser.parseFrame(Buffer.from(payloadString, 'hex'), Buffer.from(''), 'unknown');
    expect(content.type).to.equal('0x47 Pulse 4 NB-IoT alarm');
    expect(content['flowValues'][0]).to.equal(3960);
    expect(content['flowValues'][1]).to.equal(0);
    expect(content['timestamp']).to.equal('2023-12-28T19:22:17.000');
  });
});
