namespace codec {
  /**
   * Decoder class.
   *
   * Main class for decoding purposes.
   * Contains declaration of all required parsers and decode() method (API entry point).
   *
   * See below for explanations on parsers.
   */
  export class Decoder extends CommonDecoder {
    constructor() {
      super();
      this.deviceType = 'comfortSerenity';
    }

    /**
     * Get active parsers

     * @param frameCode frame code
     */
    protected override getActiveParsers(frameCode: number): FrameParser[] {
      let activeParsers: FrameParser[] = [];

      // status byte parser
      const statusByteParsers = new ComfortSerenityStatusByteParser();

      // Find the specific parser
      let dataParser: FrameParser;

      switch (frameCode) {
        case 0x10:
          dataParser = new ComfortSerenity0x10Parser();
          break;
        case 0x30:
          dataParser = new ComfortSerenity0x30Parser();
          break;
        case 0x38:
          dataParser = new ComfortSerenity0x38Parser();
          break;
        case 0x6a:
          dataParser = new ComfortSerenity0x6aParser();
          break;
        case 0x6b:
          dataParser = new ComfortSerenity0x6bParser();
          break;
        case 0x6f:
          dataParser = new ComfortSerenity0x6fParser();
          break;
        case 0x70:
          dataParser = new ComfortSerenity0x70Parser();
          break;
        case 0x1f:
          dataParser = new Generic0x1fParser();
          break;
        case 0x20:
          dataParser = new Generic0x20Parser();
          break;
        case 0x33:
          dataParser = new Generic0x33Parser();
          break;
        case 0x37:
          dataParser = new Generic0x37Parser();
          break;
        case 0x51:
          dataParser = new Generic0x51Parser();
          break;
        case 0x52:
          dataParser = new Generic0x52Parser();
          break;
        default:
          return activeParsers;
      }

      activeParsers = activeParsers.concat(statusByteParsers);
      activeParsers = activeParsers.concat(dataParser);

      // Return active parser
      return activeParsers;
    }
  }
}
