namespace codec {
  /**
   * Decoder class.
   *
   * Main class for decoding purposes.
   * Contains declaration of all required parsers and decode() method (API entry point).
   *
   * See below for explanations on parsers.
   */
  export class Decoder extends CommonDecoder {
    constructor() {
      super();
      this.deviceType = 'pulse4';
    }

    /**
     * Get active parsers

     * @param frameCode frame code
     */
    protected override getActiveParsers(frameCode: number): FrameParser[] {
      let activeParsers: FrameParser[] = [];

      // status byte parser
      const statusByteParsers = new PulseV4StatusByteParser();

      // Find the specific parser
      let dataParser: FrameParser;

      switch (frameCode) {
        case 0x10:
          dataParser = new PulseV40x10Parser();
          break;
        case 0x11:
          dataParser = new PulseV40x11Parser();
          break;
        case 0x12:
          dataParser = new PulseV40x12Parser();
          break;
        case 0x30:
          dataParser = new PulseV40x30Parser();
          break;
        case 0x46:
          dataParser = new PulseV40x46Parser();
          break;
        case 0x47:
          dataParser = new PulseV40x47Parser();
          break;
        case 0x5a:
          dataParser = new PulseV40x5aParser();
          break;
        case 0x5b:
          dataParser = new PulseV40x5bParser();
          break;
        case 0x20:
          dataParser = new Generic0x20Parser();
          break;
        case 0x33:
          dataParser = new Generic0x33Parser();
          break;
        default:
          return activeParsers;
      }

      activeParsers = activeParsers.concat(statusByteParsers);
      activeParsers = activeParsers.concat(dataParser);

      // Return active parser
      return activeParsers;
    }
  }
}
